 const sidebar = "<!DOCTYPE html>\n<html lang=\"en\">\n<head>\n    <meta charset=\"UTF-8\">\n    <title>{{name}}'s Resume</title>\n    <link rel=\"stylesheet\" href=\"https://cdnjs.cloudflare.com/ajax/libs/font-awesome/5.15.1/css/all.min.css\">\n    <style>\n        body {\n            font-family: 'Arial', sans-serif;\n            color: #333;\n            line-height: 1.15;\n            margin: 0;\n            padding: 0;\n        }\n        .resume {\n            display: flex;\n            width: 800px;\n            margin: auto;\n            background: #fff;\n        }\n        .sidebar {\n            width: 250px;\n            background-color: #4A90E2;\n            color: #fff;\n            padding: 20px;\n            height: 96vh;\n        }\n        .main-content {\n            flex-grow: 1;\n            padding: 24px;\n        }\n        .header {\n            text-align: center;\n        }\n        .header h1 {\n            margin: 0;\n            font-size: 28px;\n        }\n        .header h2 {\n            margin: 0;\n            font-weight: normal;\n            font-size: 16px;\n        }\n        .contact-info p, .contact-info i {\n            margin: 4px 0;\n            display: flex;\n            align-items: center;\n        }\n        .contact-info i {\n            margin-right: 10px;\n        }\n        .contact-info {\n            margin-top: 40px;\n        }\n        .section-title {\n            border-bottom: 2px solid #4A90E2;\n            color: #4A90E2;\n            font-size: 20px;\n            margin: 8px 0;\n            padding-bottom: 4px;\n        }\n        .detail-list li, .skills-list li {\n            margin-bottom: 8px;\n            font-size: 16px;\n            line-height: 1.5;\n        }\n        .detail-list .title {\n            font-weight: bold;\n        }\n        .detail-list .term {\n            font-style: italic;\n            color: #666;\n        }\n        .skills-list {\n            list-style: none;\n            padding-left: 0;\n            margin-top: 0;\n            overflow: hidden;\n        }\n    </style>\n</head>\n<body>\n    <div class=\"resume\">\n        <div class=\"sidebar\">\n            <div class=\"header\">\n                <h1>{{name}}</h1>\n                <h2>{{title}}</h2>\n            </div>\n            <div class=\"contact-info\">\n                {{#if email}}<p><i class=\"fas fa-envelope\"></i> {{email}}</p>{{/if}}\n                {{#if phone}}<p><i class=\"fas fa-phone\"></i> {{phone}}</p>{{/if}}\n                {{#if linkedIn}}<p><i class=\"fab fa-linkedin\"></i> {{linkedIn}}</p>{{/if}}\n                {{#if website}}<p><i class=\"fa fa-link\"></i> {{website}}</p>{{/if}}\n            </div>\n        </div>\n        <div class=\"main-content\">\n            {{#if summary}}\n            <div class=\"section\">\n                <h3 class=\"section-title\">Summary</h3>\n                <p>{{summary}}</p>\n            </div>\n            {{/if}}\n            {{#if experiences}}\n            <div class=\"section\">\n                <h3 class=\"section-title\">Experience</h3>\n                <ul class=\"detail-list\">\n                    {{#each experiences}}\n                    <li>\n                        <div class=\"title\">{{title}} - {{company}}</div>\n                        <div class=\"term\">{{startDate}} - {{endDate}}</div>\n                        <ul>\n                            {{#each description}}\n                            <li>{{this}}</li>\n                            {{/each}}\n                        </ul>\n                    </li>\n                    {{/each}}\n                </ul>\n            </div>\n            {{/if}}\n            {{#if education}}\n            <div class=\"section\">\n                <h3 class=\"section-title\">Education</h3>\n                <ul class=\"detail-list\">\n                    {{#each education}}\n                    <li>\n                        <div class=\"title\">{{degree}}</div>\n                        <div class=\"term\">{{school}} - {{graduationYear}}</div>\n                    </li>\n                    {{/each}}\n                </ul>\n            </div>\n            {{/if}}\n            {{#if skills}}\n            <div class=\"section\">\n                <h3 class=\"section-title\">Skills</h3>\n                <ul class=\"skills-list\">\n                    {{#each skills}}\n                    <li>{{this}}</li>\n                    {{/each}}\n                </ul>\n            </div>\n            {{/if}}\n        </div>\n    </div>\n</body>\n</html>\n";

export { sidebar as default };
