 const modern = "<html>\n  <head>\n    <title>{{name}}'s Resume</title>\n    <style>\n      /* Basic Layout */\n      body {\n        font-family: 'Georgia', serif;\n        font-size: 16px;\n        color: #3a3a3a;\n        line-height: 1.6;\n        margin: 0;\n        padding: 0;\n      }\n\n      /* Resume container with subtle shadow */\n      .resume {\n        max-width: 800px;\n        margin: auto;\n        background: #ffffff;\n        padding: 15px;\n      }\n\n      /* Darker header with reduced padding */\n      .resume-header {\n        background-color: #333366; /* Darker shade */\n        color: #ffffff;\n        text-align: center;\n        padding: 15px 0;\n      }\n\n      .resume-header h1 {\n        font-size: 28px;\n        margin: 0;\n      }\n\n      .summary-padding {\n        padding-left: 15px;\n      }\n\n      .resume-header p {\n        font-size: 17px;\n        margin: 5px 0;\n      }\n\n      /* Contact Information with reduced margin */\n      .contact-info {\n        text-align: center;\n        font-size: 16px;\n        color: #333366; /* Darker color */\n        margin-bottom: 15px;\n      }\n\n      /* Stylish section headings with tighter layout */\n      h2 {\n        font-size: 20px;\n        background-color: #333366; /* Consistent with the header */\n        color: white;\n        margin: 10px 0 10px; /* Reduced margin */\n        padding: 5px 15px; /* Adjusted padding */\n      }\n\n      /* Experience and Education Sections */\n      .job, .school {\n        border-left: 4px solid #333366; /* Consistent border color */\n        padding-left: 10px; /* Reduced padding */\n        margin-bottom: 15px; /* Reduced margin */\n      }\n\n      .job h3, .school h3 {\n        font-size: 17px;\n        margin-top: 0;\n      }\n\n      .date {\n        font-size: 15px;\n        color: #777;\n        font-style: italic;\n      }\n\n      ul {\n        list-style-type: square;\n        padding-left: 20px; /* Reduced padding */\n      }\n\n      ul li {\n        margin-bottom: 8px; /* Reduced margin */\n      }\n\n      /* Skills section with adjusted margins */\n      .skills h2 {\n        margin-bottom: 5px; /* Reduced bottom margin for the header */\n      }\n\n      .skills ul {\n        padding: 0;\n        text-align: left;\n        margin-top: 0; /* Remove top margin */\n        margin-bottom: 15px; /* Existing bottom margin */\n      }\n\n      .skills li {\n        display: inline-block;\n        margin: 5px 10px; /* Existing margin */\n        background-color: #f2f2f2;\n        padding: 5px 15px;\n        border-radius: 15px;\n        font-size: 16px;\n      }\n\n    </style>\n  </head>\n  <body>\n    <div class=\"resume\">\n      <header class=\"resume-header\">\n        <h1>{{name}}</h1>\n        <p>{{title}}</p>\n      </header>\n\n      <div class=\"contact-info\">\n        <p>{{#if email}}Email: {{email}}{{/if}}\n          {{#if phone}}| Phone: {{phone}}{{/if}}\n          {{#if linkedIn}}| LinkedIn: {{linkedIn}}{{/if}}\n          {{#if website}}| Website: {{website}}{{/if}}</p>\n      </div>\n\n      {{#if summary}}\n        <section class=\"summary\">\n          <h2>Summary</h2>\n          <p class=\"summary-padding\">{{summary}}</p>\n        </section>\n      {{/if}}\n\n      {{#if experiences}}\n        <section class=\"experience\">\n          <h2>Experience</h2>\n          {{#each experiences}}\n            <div class=\"job\">\n              <h3>{{title}} - {{company}}</h3>\n              <p class=\"date\">{{startDate}} - {{endDate}}</p>\n              <ul>\n                {{#each description}}\n                  <li>{{this}}</li>\n                {{/each}}\n              </ul>\n            </div>\n          {{/each}}\n        </section>\n      {{/if}}\n\n      {{#if education}}\n        <section class=\"education\">\n          <h2>Education</h2>\n          {{#each education}}\n            <div class=\"school\">\n              <h3>{{degree}}</h3>\n              <p class=\"date\">{{school}} - {{graduationYear}}</p>\n            </div>\n          {{/each}}\n        </section>\n      {{/if}}\n\n      {{#if skills}}\n        <section class=\"skills\">\n          <h2>Skills</h2>\n          <ul>\n            {{#each skills}}\n              <li>{{this}}</li>\n            {{/each}}\n          </ul>\n        </section>\n      {{/if}}\n    </div>\n  </body>\n</html>\n";

